svg.script:
//


		var p1;
		var np=25;

		var pathMove='M';

		var pathQuadBez=' Q';

		var pathComma=',';

		var pSp=' ';

		var pDZ='0,0';

		var pD=[];

		function init(evt){
			p1=document.getElementById("path");
			document.addEventListener('mousemove',function(){aP(event)} );
			document.addEventListener('touchmove',function(){ event.preventDefault(); aP(event.targetTouches[0])});

		}


		function aP(e){ 
			pD.push([e.pageX,e.pageY]);
			if(pD.length>np){
				rP()
			};
			pU();
		};


		function mP(p2,p1,f){
			return [p1[0]+(p2[0]-p1[0])*f , p1[1]+(p2[1]-p1[1])*f]
		};

		function rP(){ 
			if(pD.length>1){ 
				pD.shift(); 
				pU(); 
			} 
		};

		function pU(){
			var nP=pD.length>1?pathMove:pathMove+pDZ;

			for( var L=pD.length-1,j=0; j<L; j++ ){
				if( j!=0 ){
					var P2=mP(pD[j],pD[j+1],.5);
					nP+=pathQuadBez+pD[j][0]+pathComma+pD[j][1]+pSp+P2[0]+pathComma+P2[1];
				}else{ 
					nP+=pSp+pD[j][0]+pathComma+pD[j][1] 
				}
			}
			p1.setAttribute("d",nP);
		}

    //
